from functools import lru_cache

@lru_cache(maxsize=None)
def fibonacci(n: int) -> int:
    """메모이제이션을 활용한 피보나치 수열 계산
    시간복잡도: O(n), 공간복잡도: O(n)
    """
    if n <= 1:
        return n
    return fibonacci(n - 1) + fibonacci(n - 2)

# 사용 예시
for i in range(10):
    print(f"F({i}) = {fibonacci(i)}")
# F(0)=0, F(1)=1, F(2)=1, ... F(9)=34
